from __future__ import print_function
import argparse
import torch
import torch.nn as nn
import torch.nn.functional as F
from torchvision import datasets, transforms
import numpy as np
import json
from json import JSONEncoder
# import onnxruntime as onnxrt

class NumpyArrayEncoder(JSONEncoder):
    def default(self, obj):
        if isinstance(obj, np.ndarray):
            return obj.tolist()
        return JSONEncoder.default(self, obj)

class Net(nn.Module):
    def __init__(self):
        super(Net, self).__init__()
        self.conv1 = nn.Conv2d(1, 32, 3, 1)
        self.conv2 = nn.Conv2d(32, 64, 3, 1)
        self.dropout1 = nn.Dropout(0.25)
        self.dropout2 = nn.Dropout(0.5)
        self.fc1 = nn.Linear(9216, 128)
        self.fc2 = nn.Linear(128, 10)

    def forward(self, x):
        x = self.conv1(x)
        x = F.relu(x)
        x = self.conv2(x)
        x = F.relu(x)
        x = F.max_pool2d(x, 2)
        x = self.dropout1(x)
        x = torch.flatten(x, 1)
        x = self.fc1(x)
        x = F.relu(x)
        x = self.dropout2(x)
        x = self.fc2(x)
        output = F.log_softmax(x, dim=1)
        return output

def mnist_preprocess():
    # # to install any additional lib
    # import subprocess
    # subprocess.run("pip install your_lib "
    #                "--index https://artifactory.gcp.anz/artifactory/api/pypi/pypi/simple/ "
    #                "--trusted-host artifactory.gcp.anz --user".split(), capture_output=True, text=True)

    device = torch.device("cpu")
    model_name = 'mnist_cnn'
    model = Net().to(device)
    model.load_state_dict(torch.load(f"models/{model_name}.pt"))
    # model.load_state_dict(torch.load("/mnts/cpd-file-1tb/models/mnist_cnn.pt"))
    model.eval()
    preprocess_assets={}
    preprocess_assets["model"]=model
    return preprocess_assets
            
def mnist_predict(input_json,preprocess_assets):
    model = preprocess_assets["model"]
    device = torch.device("cpu")

    # load data from input json
    input_data= input_json['input_data']
    data1D = input_data[0]['values'][0][0]
    data4D = np.reshape(data1D, (1, 1, 28, 28))
    data4D = np.float32(data4D)

    x_np = torch.from_numpy(data4D)
    x_np = x_np.to(device)

    output = model(x_np)
    pred = output.argmax(dim=1, keepdim=True)  # get the index of the max log-probability

    output1D = output.cpu().detach().numpy().ravel()
    pred = pred.cpu().numpy().ravel()
    print('used pytorch')

    # Serialization
    numpyData = {
        "fields": [
            "prediction_classes",
            "probability"
        ],
        "values": [[pred, output1D]]
    }
    output_json_str = json.dumps(numpyData, cls=NumpyArrayEncoder)  # use dump() to write array into file
    # print("Printing JSON serialized NumPy array")
    # print('output_json=\n',output_json)
    
    return output_json_str


